import { connectToStore } from "@applicaster/zapp-react-native-redux";
import {
  setAppReady,
  setAppNotReady,
} from "@applicaster/zapp-react-native-redux/appState";
import * as R from "ramda";
import { URLSchemeHandlerComponent } from "./URLSchemeHandler";
import { withNavigator } from "@applicaster/zapp-react-native-ui-components/Decorators/Navigator";

const reduxConnector = connectToStore(
  R.pick(["rivers", "remoteConfigurations"]),
  { setAppReady, setAppNotReady }
);

export const URLSchemeHandler = R.compose(
  reduxConnector,
  withNavigator
)(URLSchemeHandlerComponent);
