import * as React from "react";
import * as R from "ramda";

import { connectToStore } from "@applicaster/zapp-react-native-redux";

import { NavigationProvider as NavigationProviderComponent } from "./NavigationProvider";

const NavigationProvider = connectToStore(R.pick(["rivers", "plugins"]))(
  NavigationProviderComponent
);

export function withNavigationProvider(Component) {
  return function WrappedWithNavigationProvider(props) {
    return (
      <NavigationProvider>
        <Component {...props} />
      </NavigationProvider>
    );
  };
}
