import * as R from "ramda";

export function getLocalizations(props) {
  const { localizations } = props;
  const locale = R.compose(
    R.head,
    R.split("-"),
    R.toLower
  )(window.navigator.language);

  return R.ifElse(
    R.has(locale),
    R.prop(locale),
    R.compose(
      R.head,
      R.values
    )
  )(localizations);
}
