import { displaySplash } from "../displaySplash";

const resolver = jest.fn();
const rejecter = jest.fn();

describe("displaySplash", () => {
  it("resolves after the provided time", async () => {
    await displaySplash(1)
      .then(resolver)
      .catch(rejecter);

    expect(resolver).toHaveBeenCalled();
    expect(rejecter).not.toHaveBeenCalled();
  });
});
