import { connectToStore } from "@applicaster/zapp-react-native-redux";
import * as R from "ramda";

import { BackgroundComponent } from "./Background";
import { DisplayStateContext, PlayerContentContext } from "../../Contexts";

const reduxConnector = connectToStore(R.pick(["remoteConfigurations", "styles"]));

export const Background = R.compose(
  reduxConnector,
  DisplayStateContext.withConsumer,
  PlayerContentContext.withConsumer
)(BackgroundComponent);
