// @flow

import React from "react";

const style = {
  color: "#000000"
};

const whichDisplay = (error, info) => {
  if (error) {
    return (
      <div style={style}>
        <div>Error: {error.message}</div>
        <div>Stack: {error.stack}</div>
        <div>Info: {info.componentStack}</div>
      </div>
    );
  } else {
    return <div style={style}>Info: {info.componentStack}</div>;
  }
};

export function DisplayError({
  error,
  info,
  sendError
}: {
  error: any,
  info: any,
  sendError: any
}): React.ComponentType<any> {
  return (
    <div>
      <div style={style}>Errors: {whichDisplay(error, info)}</div>;
    </div>
  );
}
