import * as R from "ramda";
import { hot } from "react-hot-loader";

import { connectToStore } from "@applicaster/zapp-react-native-redux";
import { ZappUIComponent } from "@applicaster/zapp-react-native-ui-components/Components/ZappUIComponent";

import { HorizontalScrollContext } from "../../Contexts";
import { HorizontalListComponent } from "./HorizontalList";

const storeConnector = connectToStore(
  R.pick(["styles", "rivers", "remoteConfigurations"])
);

export const HorizontalList = R.compose(
  hot(module),
  storeConnector,
  HorizontalScrollContext.withProvider,
  HorizontalScrollContext.withConsumer,
  ZappUIComponent
)(HorizontalListComponent);
