// @flow
import * as React from "react";

import { Text as RnText } from "react-native";

import { styleConverter } from "./styleConverter";

type Props = {
  style: {},
  label: string,
  [string]: any,
};

const baseStyles = {
  display: "flex",
  flex: 1,
  flexDirection: "column",
  fontFamily: "Helvetica, Arial, Sans-serif",
  fontSize: 18,
};

export function Text({ style, label, ...otherProps }: Props) {
  return (
    <RnText
      style={styleConverter(style, baseStyles, otherProps)}
      {...otherProps}
    >
      {label}
    </RnText>
  );
}
