// @flow
import * as React from "react";

import { styleConverter } from "./styleConverter";

type Props = {
  children: React.ComponentType<any>,
  [string]: any,
  style: {},
};

const baseStyles = {
  display: "flex",
  flex: 1,
  flexDirection: "column",
};

export function View({ children, style, ...restOfProps }: Props) {
  return (
    <div style={styleConverter(style, baseStyles)} {...restOfProps}>
      {children}
    </div>
  );
}
