// @flow
import * as React from "react";
import * as R from "ramda";
import { River } from "../../River";
import { screenContainerStyle } from "../styles";
import { View } from "react-native";

type Props = {
  targetRiverId: string,
  selectedItem: { id: string },
  preferredFocus: ?boolean,
  componentId: ?string,
};

export class ScreenContainer extends React.Component<Props> {
  render() {
    const { selectedItem, targetRiverId, componentId } = this.props;

    const feedUrl = R.path(["content", "src"], selectedItem);
    if (!selectedItem) {
      return null;
    }
    return (
      <View style={screenContainerStyle}>
        <River
          key={selectedItem.id}
          screenId={targetRiverId}
          feedUrl={feedUrl}
          isInsideContainer={true}
          groupId={componentId}
        />
      </View>
    );
  }
}
