import { fetchImageFromMetaByKey } from "../fetchImageFromMetaByKey";

const meta = {
  media_group: [
    {
      type: "image",
      media_item: [
        {
          src: "https://images.applicaster.com/imageBase",
          type: "image",
          key: "image_base",
        },
      ],
    },
  ],
};

describe("fetchImageFromMetaByKey", () => {
  it("Pull correct image with the key 'image_base' from the meta data", () => {
    expect(fetchImageFromMetaByKey("image_base", meta)).toEqual(
      "https://images.applicaster.com/imageBase"
    );
  });
});
