import * as R from "ramda";

export function getComponentCellStyle(component) {
  const componentCellStyle =
    R.path(["styles", "cell_styles", 0], component) || "HORIZONTAL_LIST_1";
  const componentStyleFamily =
    R.path(["styles", "family"], component) || "FAMILY_DANUBE";

  return `${componentStyleFamily}_${componentCellStyle}`;
}
