// @flow
import * as React from "react";
import { View } from "react-native";

import { River } from "../../River";
import { screenContainerStyle } from "../styles";

type Props = {
  targetRiverId: string,
  selectedItem: {},
  componentId: string,
  focused: boolean,
  parentFocus: {
    nextFocusDown: React.Ref,
    nextFocusRight: React.Ref,
    nextFocusLeft: React.Ref,
    nextFocusUp: React.Ref,
  },
};

export function ScreenContainer({
  selectedItem,
  targetRiverId,
  componentId,
  focused,
  parentFocus,
}: Props) {
  const feedUrl = selectedItem?.content?.src;

  if (!selectedItem) {
    return null;
  }

  return (
    <View style={screenContainerStyle}>
      <River
        focused={focused}
        parentFocus={parentFocus}
        screenId={targetRiverId}
        feedUrl={feedUrl}
        isInsideContainer={true}
        groupId={componentId}
      />
    </View>
  );
}
