import { NativeModules } from "react-native";

import { parseJsonIfNeeded } from "@applicaster/zapp-react-native-utils/functionUtils";

// eslint-disable-next-line prefer-promise-reject-errors
const nullPromise = () => Promise.reject("SessionStorage bridge is null");
const defaultSessionStorage = {
  setItem: nullPromise,
  getItem: nullPromise,
  getAllItems: nullPromise,
};
const { SessionStorage = defaultSessionStorage } = NativeModules;

export const sessionStorage = {
  /**
   * Set value to session storage dict for key in namespace domain
   * @param  {String} key  The key with which to associate the value.
   * @param  {String} value The object to store in storage.
   * @param  {String} namespace Namespace that will be used for save item.
   *  If namespace === null. Default namespace `applicaster.v2`
   */
  setItem(key, value, namespace = null) {
    if (key && value) {
      return SessionStorage.setItem(key, JSON.stringify(value), namespace);
    }
  },

  /**
   * Get value from session storage dict for key in namespace domain
   * @param  {String} key  The key with which to associate the value.
   * @param  {String} namespace Namespace that will be used for save item.
   *  If namespace === null. Default namespace `applicaster.v2`
   */
  getItem(key, namespace = null) {
    return SessionStorage.getItem(key, namespace).then(parseJsonIfNeeded);
  },

  /**
   * Get All values from session storage dict for key in namespace domain
   */
  getAllItems(namespace = null) {
    return SessionStorage.getAllItems(namespace)
      .then(parseJsonIfNeeded)
      .catch(error => {
        throw error;
      });
  },
};
