function getAlias(isProductionEnv) {
  if (isProductionEnv) {
    return {};
  }

  try {
    return require("../package.json").applicaster.moduleMap;
  } catch (e) {
    // eslint-disable-next-line no-console
    console.warn(e);
    return {};
  }
}

const config = (api) => {
  const alias = getAlias(api.env("production"));

  return {
    presets: [["@applicaster/quick-brick", { platform: "native", alias }]],
  };
};

module.exports = config;
