// @flow
import * as R from "ramda";

type PluginConfiguration = {
  plugin: {
    identifier: string,
    [string]: any,
  },
  configuration_json: any,
};

type PluginConfigurationsMap = {
  [string]: PluginConfiguration,
};

export function pluginConfigurationsLoader(
  pluginConfigurations: [PluginConfiguration]
): PluginConfigurationsMap {
  return R.chain(R.zipObj, R.map(R.path(["plugin", "identifier"])))(
    pluginConfigurations
  );
}
