// @flow
import * as R from "ramda";

/**
 * unwraps default export if needed
 *
 * @param {Array<Plugin} plugins from app Context
 * @returns {Array<Plugin>} array of plugin pointing to the relevant property
 */

export function pluginsLoader(plugins: [any]): [any] {
  return R.map(
    R.evolve({ module: R.when(R.has("default"), R.prop("default")) }),
    plugins
  );
}
