import { riversLoader } from "../index";

const rivers = [
  { id: "A1234", name: "river 1" },
  { id: "B4567", name: "river 2" },
];

describe("riversLoader", () => {
  it("creates a map where each river is prefixed with its id", () => {
    const loadedRivers = riversLoader(rivers);
    expect(loadedRivers).toEqual(
      expect.objectContaining({
        [rivers[0].id]: rivers[0],
        [rivers[1].id]: rivers[1],
      })
    );
  });
});
