// @flow
import * as R from "ramda";

/**
 * transforms Rivers array to prefix each river with its id
 *
 * @export
 * @param {Array<River>} rivers from app Context
 * @returns {Object} rivers map with { [river.id]: river }
 */
export function riversLoader(rivers: Array<any>): { [string]: any } {
  return R.zipObj(R.map(R.prop("id"), rivers), rivers);
}
