import { actionCreator } from "../index";

describe("actionCreator", () => {
  it("returns an action with the proper type and payload", () => {
    const TYPE = "ACTION_TYPE";
    const payload = { foo: "bar" };
    const meta = { remote: true };

    const action = actionCreator(TYPE, payload, meta);

    expect(action).toHaveProperty("type", TYPE);
    expect(action).toHaveProperty("payload", payload);
    expect(action).toHaveProperty("meta", meta);
  });
});
