/**
 * Returns an action object See [action creators]{@link https://daveceddia.com/redux-action-creators/}
 *
 * @export
 * @param {string} type of the action
 * @param {object} payload (optional)
 * @param {object} meta (optional) extra properties required for the action
 * @returns {object} redux action object
 */
export function actionCreator(type, payload, meta) {
  return { type, payload, meta };
}
