import { isValidAnalyticsData } from "../";

describe("isValidAnalyticsData", () => {
  it("checkFunc", () => {
    expect(isValidAnalyticsData("TestEvent", { "1": 2 })).toBe(true);
    expect(isValidAnalyticsData("TestEvent", null)).toBe(true);
    expect(isValidAnalyticsData("TestEvent", undefined)).toBe(true);
    expect(isValidAnalyticsData("TestEvent", 1)).toBe(false);
    expect(isValidAnalyticsData("TestEvent", "wrong_value")).toBe(false);
    expect(isValidAnalyticsData(1, { "1": 2 })).toBe(false);
    expect(isValidAnalyticsData({ "1": 2 }, { "1": 2 })).toBe(false);
    expect(isValidAnalyticsData(null, { "1": 2 })).toBe(false);
    expect(isValidAnalyticsData(undefined, { "1": 2 })).toBe(false);
  });
});
