import * as R from "ramda";
import { findPluginByType } from "@applicaster/zapp-react-native-utils/pluginUtils";

export function getPlugins(props) {
  const { plugins } = props;

  return {
    Player: findPluginByType("player", plugins),
    PlayerControls: findPluginByType("player_controls", plugins) || null,
    PlayerLoadingView: findPluginByType("player_loading_view", plugins),
  };
}

export function getHomeId(rivers) {
  return R.compose(
    R.prop("id"),
    R.find(R.prop("home")),
    R.values
  )(rivers);
}
