// @flow
import * as React from "react";
import { View, Image, Text } from "react-native";
import * as styles from "./styles";

type Props = {
  logo: ?string,
  title: string,
  subtitle: string,
};

export function ProgramInfo({ logo, title = "", subtitle = "" }: Props) {
  return (
    <View style={styles.container}>
      <View style={styles.programInfo.container}>
        {logo && (
          <Image source={{ uri: logo }} style={styles.programInfo.logo} />
        )}
        <View style={styles.programInfo.title}>
          <Text style={styles.programInfo.titleText}>{title}</Text>
        </View>
        <View style={styles.programInfo.subtitle}>
          <Text style={styles.programInfo.subtitleText}>{subtitle}</Text>
        </View>
      </View>
    </View>
  );
}
