// @flow
import React from "react";
import { View, Image } from "react-native";

type Props = {
  srcImage: {},
  config: {
    titleColor: "string",
    summaryColor: "string",
    backgroundColor: "string",
    backgroundImage: "string",
    isRTL: boolean,
  },
};

const imageStyles = {
  width: 128,
  height: 72,
};

const containerStyles = {
  ...imageStyles,
  marginBottom: 30,
};

export function Channel({ srcImage, config }: Props) {
  return (
    <View style={containerStyles}>
      <Image source={{ uri: srcImage }} style={imageStyles} />
    </View>
  );
}
