// @flow
import React from "react";
import { View, Text } from "react-native";
import { directionStyles } from "./helpers";

type Props = {
  config: {
    titleColor: "string",
    summaryColor: "string",
    backgroundColor: "string",
    backgroundImage: "string",
    isRTL: boolean,
  },
  start_time: ?string,
  end_time: ?string,
};

const containerStyles = ({ isRTL }) => ({
  width: 600,
  height: 40,
  justifyContent: directionStyles(isRTL).justifyContent,
});

const textStyles = ({ summaryColor }) => ({
  color: summaryColor,
  opacity: 0.8,
  fontSize: 20,
});

export function Runtime({ start_time, end_time, config }: Props) {
  return (
    <View style={containerStyles}>
      {!!start_time && !!end_time && (
        <Text style={textStyles(config)}>{`${start_time} - ${end_time}`}</Text>
      )}
    </View>
  );
}
