import * as R from "ramda";

import { connectToStore } from "@applicaster/zapp-react-native-redux";

import { AudioPlayer as AudioPlayerComponent } from "./AudioPlayer";

const storeConnector = connectToStore(
  R.compose(
    ({ remoteConfigurations: { styles } }) => ({ styles }),
    R.pick(["remoteConfigurations"])
  )
);

export const AudioPlayer = storeConnector(AudioPlayerComponent);
