import * as React from "react";
import { View, Text, ScrollView } from "react-native";
import * as R from "ramda";

const style = { flex: 1 };

export const ContentScreen = props => {
  console.log(props); // eslint-disable-line no-console
  const printableProps = R.tryCatch(
    p => JSON.stringify(p, null, 2),
    R.flip(R.identity)
  )(props);

  return (
    <View style={style}>
      <ScrollView>
        <Text>I'm a content screen</Text>
        <Text>my props are : </Text>
        <Text>{printableProps}</Text>
      </ScrollView>
    </View>
  );
};
