// @flow
import * as React from "react";

type Props = {
  parentGroupId: string,
  setParentFocusableGroupId: () => void,
  children: any,
};

export class FocusableGroupContextWrapperComponent extends React.PureComponent<Props> {
  constructor(props) {
    super(props);
    const { setParentFocusableGroupId, parentGroupId } = this.props;
    setParentFocusableGroupId(parentGroupId);
  }
  render() {
    const { children } = this.props;
    return <React.Fragment>{children}</React.Fragment>;
  }
}
