/* eslint-disable no-extra-parens */
import React from "react";
import { ActivityIndicator, View } from "react-native";
import { getAppStylesColor } from "@applicaster/zapp-react-native-utils/stylesUtils";

type Props = {
  styles: {},
  [string]: any,
};

const bufferStyles = {
  flex: 1,
  width: "100%",
  height: "100%",
  alignItems: "center",
  justifyContent: "center",
  paddingTop: 500,
};

const bufferFocusableGroupName = "PlayerWrapper-BufferAnimationComponent";

export function BufferAnimationComponent(props: Props) {
  const styles = props?.styles;
  const appStyles = styles?.universal;
  const color = getAppStylesColor("loading_spinner_color", appStyles);

  return (
    <View id={bufferFocusableGroupName} style={bufferStyles}>
      <ActivityIndicator color={color} size="large" {...props} />
    </View>
  );
}
