import * as R from "ramda";
import { connectToStore } from "@applicaster/zapp-react-native-redux";

import { withRiver } from "../../Decorators/RiverResolver";
import { withNavigator } from "../../Decorators/Navigator";
import { ContainerContext } from "../../Contexts";
import { RiverComponent } from "./River";

const reduxConnector = connectToStore(R.pick(["rivers"]));

export const River = R.compose(
  reduxConnector,
  withRiver,
  withNavigator,
  ContainerContext.withConsumer
)(RiverComponent);
