import * as React from "react";

import { ViewportTracker as ViewportTrackerComponent } from "./ViewportTracker";
import { ViewportEventContext, viewportEventsManager } from "../ViewportEvents";

export function ViewportTracker(props) {
  return (
    <ViewportEventContext.Provider value={viewportEventsManager}>
      <ViewportTrackerComponent {...props} />
    </ViewportEventContext.Provider>
  );
}
