import * as R from "ramda";
import { createContext } from "@applicaster/zapp-react-native-utils/reactUtils/createContext";

// This variable is ussing to pass information to focusable group that group has parrent
const PARENT_FOCUSABLE_GROUP_ID = "parentFocusableGroupId";

const initialContext = {
  parentFocusableGroupId: null,
};

function stateValidator({ property, value }, previousValue) {
  if (property === PARENT_FOCUSABLE_GROUP_ID) {
    return R.either(R.is(String), R.isNil)(value);
  }
  return false;
}

export const FocusableContext = createContext(initialContext, stateValidator);
