/* global tizen */
const isTizen = typeof tizen !== "undefined";
import * as platformKeys from "./keysByPlatform";
import { Platform } from "react-native";

if (isTizen) {
  tizen.tvinputdevice.registerKeyBatch([
    "MediaPlayPause",
    "MediaPlay",
    "MediaStop",
    "MediaFastForward",
    "MediaRewind",
    "3D",
    "Exit",
    "MediaPause",
  ]);
}

/*
 * Default web keys are replaced by platform specific keys
 * platformKeys[Platform.OS] should only include keys
 * that are unique to that platform, i.e. Exit: { keyCode: 10182 }
 */
export const KEYS = Object.assign(
  platformKeys["web"],
  platformKeys[Platform.OS]
);

export const ARROW_KEYS = [
  KEYS.ArrowUp,
  KEYS.ArrowLeft,
  KEYS.ArrowDown,
  KEYS.ArrowRight,
];
