import * as R from "ramda";

const CUSTOM_KEY = "other";

/**
 * Gets key for label if other return custom key, otherwise returns selected data key.
 * @param {String} data_key selected data key for label.
 * @param {String} custom_key custom data key for label.
 * @returns {String[]} keys splitted by '.' separator
 */
export const getKeyForLabel = (data_key, custom_key) => {
  return R.split(".", data_key === CUSTOM_KEY ? custom_key : data_key);
};
