// @flow
import { useRef, useEffect } from "react";

/**
 * This hook returns a previous value that was passed to it.
 * Eg. If on first render, you pass a "1" and on second render "2" the hook will return "1"
 * Hook is meant to mimic prevProps/prevState values that would be passed to the ComponenDidupdate function.
 * @param {*} value
 * @return {*}
 */
export const usePrevious = (value: any): any => {
  const ref = useRef();
  useEffect(() => {
    ref.current = value;
  });
  return ref.current;
};
