import * as React from "react";
import { View, Text } from "react-native";

export class ClassComponent extends React.Component {
  render() {
    return (
      <View>
        <Text>I'm a class component</Text>
      </View>
    );
  }
}

export const existingMethod = jest.fn();

export class ComponentWithLifeCycle extends React.Component {
  componentDidMount() {
    existingMethod();
  }

  render() {
    return <StatelessComponent />;
  }
}

export const StatelessComponent = () => (
  <View>
    <Text>I'm a stateless component</Text>
  </View>
);

export const componentDidMount = jest.fn();
export const COMPONENT_DID_MOUNT_METHOD = "componentDidMount";
