// @flow
import * as R from "ramda";

/**
 * Function to be used in tandem with enzyme findWhere(node => node.prop('testID') === 'yourID')
 * Example:
 * wrapper.findWhere(testIDEquals('yourID'))
 * @param {*} testId
 * @return boolean
 */
export const testIDEquals = (testId: any): boolean =>
  R.compose(R.equals(testId), R.invoker(1, "prop")("testID"));
