const { taskRunner } = require("../../taskRunner");

const { configurator } = require("./configurator");
const { runApp } = require("./runApp");

const runTask = {
  name: "run",
  startMessage: "Running the app",
  prerequisitesChecker: () => Promise.resolve(true),
  configurator,
  steps: [
    {
      start: "Running the native app",
      run: runApp,
      error: "Couldn't start the app",
      completion: "all set !",
    },
  ],
};

module.exports = { runApp: taskRunner(runTask) };
